import React from "react";
import { Card } from "antd";
import {
  RetweetOutliend,
  HeartOutliend,
  MessageOutliend,
  Popover,
  EllipsisOutliend,
} from "@ant-design";
import ButtonGroup from "antd/lib/button/button-group";

const PostCard = ({ post }) => {
  return (
    <div>
      <Card
        cover={
          post.Images[0] && (
            <PostImaegs
              images={post.Images}
              actions={[
                <RetweetOutliend key="retweet" />,
                <HeartOutliend key="heart" />,
                <MessageOutliend key="comment" />,
                <Popover key="more" content={[<ButtonGroup></ButtonGroup>]}>
                  <EllipsisOutliend />
                </Popover>,
              ]}
            />
          )
        }
      >
        <Content />
      </Card>
      <CommentForm />
      <Comments />
    </div>
  );
};

export default PostCard;
