import React, { useState } from "react";
import { PropTypes } from "prop-types";
import Slick from "react-slick";
import styled, { createGlobalStyle } from "styled-components";

const Overlay = styled.div`
  /* fixed 전체 화면 채우는 방법 */
  position: fixed;
  z-index: 5000;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
`;

const Header = styled.header`
  height: 33px;
  background-color: white;
  position: relative;
  padding: 0;
  text-align: center;

  & h1 {
    margin: 0;
    font-size: 17px;
    color: #333;
    line-height: 44px;
  }

  & button {
    position: absolute;
    right: 0;
    top: 0;
    padding: 15px;
    line-height: 14px;
    cursor: pointer;
  }
`;

const SlickWrapper = styled.div`
  height: calc(100% - 44px);
  background-color: #090909;
`;

const ImgWrapper = styled.div`
  padding: 32px;
  text-align: center;

  & img {
    margin: 0 auto;
    max-height: 750px;
  }
`;

const Indicator = styled.div`
  text-align: center;

  & > div {
    display: inline-block;
    width: 75px;
    height: 30px;
    line-height: 30px;
    color: white;
    font-size: 15px;
    text-align: center;
    border-radius: 15px;
    background-color: #313131;
  }
`;

const Global = createGlobalStyle`
    .slick-slide {
        display:inline-block;
    }
`;

const ImagesZoom = ({ images, onClose }) => {
  const [currentSlide, setCurrentSlide] = useState(0); // 현재 슬라이드 번호 저장

  return (
    <Overlay>
      <Header>
        <h1>상세 이미지</h1>
        <button onClick={onClose}>X</button>
      </Header>
      <SlickWrapper>
        <div>
          <Slick
            initialSlide={0} // 첫 번째 나올 이미지는 index 몇번으로 할 것인지
            afterChange={(slide) => setCurrentSlide(slide)} // 페이지를 넘기면 slide 번호를 state로 저장
            infinite // 무한 반복
            arrows={false} // 화살표 지우기 (손으로만 슬라이드 가능)
            slidesToShow={1} // 한번에 하나씩만 보이도록
            slidesToScroll={1} // 한번에 하나씩만 넘길 수 있도록
          >
            {/* Slick이 자동으로 Slick안에 있는 div들을 캐러셀로 만들어준다. */}
            {images.map((v) => (
              <ImgWrapper key={v.src}>
                <img src={v.src} alt={v.src} />
              </ImgWrapper>
            ))}
          </Slick>
        </div>
      </SlickWrapper>
    </Overlay>
  );
};

ImagesZoom.PropTypes = {
  images: PropTypes.arrayOf(PropTypes.object).isRequired,
  onClose: PropTypes.func.isRequired,
};

export default ImagesZoom;
