import React from "react";
import { Card } from "antd";

const PostCard = ({ post }) => {
  return (
    <div>
      <Card cover={post.Images[0] && <PostImaegs images={post.Images} />}>
        <Content />
      </Card>
      <CommentForm />
      <Comments />
    </div>
  );
};

export default PostCard;
